document.addEventListener("DOMContentLoaded", function () {
  // Smooth scrolling for anchor links
  const links = document.querySelectorAll('a[href^="#"]');
  for (const link of links) {
    link.addEventListener("click", function (event) {
      event.preventDefault();
      const targetId = link.getAttribute("href").substring(1);
      const targetElement = document.getElementById(targetId);
      if (targetElement) {
        window.scrollTo({
          top: targetElement.offsetTop,
          behavior: "smooth",
        });

        // Close mobile menu if it's open
        if (menu.classList.contains("active")) {
          menu.classList.remove("active");
          mobileMenuClose.style.display = "none";
          document.body.classList.remove("active");
        }
      }
    });
  }

  // Mobile menu toggle
  const menu = document.getElementById("menu");
  const mobileMenuOpen = document.createElement("div");
  mobileMenuOpen.id = "mobile-menu-open";
  mobileMenuOpen.innerHTML = '<i class="fa fa-bars" aria-hidden="true"></i>';
  document.body.appendChild(mobileMenuOpen);

  const mobileMenuClose = document.getElementById("mobile-menu-close");

  mobileMenuOpen.addEventListener("click", function () {
    menu.classList.add("active");
    mobileMenuClose.style.display = "block";
    document.body.classList.add("active");
  });

  mobileMenuClose.addEventListener("click", function () {
    menu.classList.remove("active");
    mobileMenuClose.style.display = "none";
    document.body.classList.remove("active");
  });

  // Hide mobile menu close button initially
  mobileMenuClose.style.display = "none";

  // Scroll down button functionality
  const scrollDownButton = document.querySelector("#lead-down span");
  scrollDownButton.addEventListener("click", function () {
    const nextSection = document.querySelector("#about");
    window.scrollTo({
      top: nextSection.offsetTop,
      behavior: "smooth",
    });
  });

  // Scroll up button functionality
  const scrollUpButton = document.querySelector("#to-top");
  scrollUpButton.addEventListener("click", function () {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  });
});
